mapper.select:
SELECT DISTINCT B.BOARD_ID, BOARD_TITLE, SUBSTR(TO_CHAR(BOARD_CONTENT), 1, 50) AS BOARD_CONTENT, TO_CHAR(REPLACE(SUBSTR(BOARD_DATE,1,8), '/', '.')) AS BOARD_DATE, B.USERS_ID, COUNT(HEART_ID) OVER(PARTITION BY B.BOARD_ID) AS HEART_SUM FROM BOARD B LEFT JOIN ATTACH A ON B.BOARD_ID = A.BOARD_ID LEFT JOIN HEART H ON B.BOARD_ID = H.HEART_ID
WHERE BOARD_CATEGORY = #{boardCategory}
ORDER BY BOARD_DATE DESC

mapper.update:
UPDATE BOARD
SET BOARD_TITLE = #{boardTitle},
    BOARD_CONTENT = #{boardContent},
    BOARD_CATEGORY = #{boardCategory}
WHERE BOARD_ID = #{boardId}
