mapper.select:
SELECT MAIN.*, A.ATTACH_NAME AS ATTACH_NAME FROM ( SELECT DISTINCT B.BOARD_ID, BOARD_TITLE, DBMS_LOB.SUBSTR(BOARD_CONTENT, 100, 1) AS BOARD_CONTENT, TO_CHAR(REPLACE(SUBSTR(BOARD_DATE,1,8), '/', '.')) AS BOARD_DATE, BOARD_CATEGORY, NVL(U.USERS_NICKNAME, B.USERS_ID) AS USERS_ID, COUNT(HEART_ID) OVER(PARTITION BY B.BOARD_ID) AS HEART_SUM FROM BOARD B LEFT JOIN HEART H ON B.BOARD_ID = H.HEART_ID JOIN USERS U ON B.USERS_ID = U.USERS_ID ) MAIN LEFT JOIN ATTACH A ON MAIN.BOARD_ID = A.BOARD_ID
WHERE BOARD_CATEGORY = #{boardCategory}
ORDER BY BOARD_DATE DESC, ATTACH_NAME NULLS LAST

mapper.update:
UPDATE BOARD
SET BOARD_TITLE = #{boardTitle},
    BOARD_CONTENT = #{boardContent},
    BOARD_CATEGORY = #{boardCategory}
WHERE BOARD_ID = #{boardId}
