mapper.select:
SELECT MAIN.*, A.ATTACH_FILE AS ATTACH_FILE FROM ( SELECT DISTINCT B.BOARD_ID, BOARD_TITLE, DBMS_LOB.SUBSTR(BOARD_CONTENT, 50, 1) AS BOARD_CONTENT, TO_CHAR(REPLACE(SUBSTR(BOARD_DATE,1,8), '/', '.')) AS BOARD_DATE, BOARD_CATEGORY, B.USERS_ID, COUNT(HEART_ID) OVER(PARTITION BY B.BOARD_ID) AS HEART_SUM FROM BOARD B LEFT JOIN HEART H ON B.BOARD_ID = H.HEART_ID ) MAIN LEFT JOIN ATTACH A ON MAIN.BOARD_ID = A.BOARD_ID
WHERE BOARD_CATEGORY = #{boardCategory}
ORDER BY BOARD_DATE DESC

mapper.update:
UPDATE BOARD
SET BOARD_TITLE = #{boardTitle},
    BOARD_CONTENT = #{boardContent},
    BOARD_CATEGORY = #{boardCategory}
WHERE BOARD_ID = #{boardId}
